function add_facebook_open_graph_tags() {
	if (is_single()) {
	$post_image = thesis_post_image_info('thumb');
	?>
	<meta property="og:title" content="<?php the_title(); ?>" />
	<meta property="og:type" content="article" />
	<meta property="og:image" content="<?php echo $post_image['url']; ?>" />
	<meta property="og:url" content="<?php the_permalink(); ?>" />
	<meta property="og:description" content="<?php echo get_bloginfo('description'); ?>" />
	<meta property="og:site_name" content="<?php echo get_bloginfo('name'); ?>" />
	<meta property="fb:admins" content="FACEBOOK USER ID HERE" />
	<?php }
}
add_action('wp_head', 'add_facebook_open_graph_tags',99);
